'use strict';

var tape = require('tape');

var _require = require('../'),
    BufferList = _require.BufferList,
    BufferListStream = _require.BufferListStream;

var _require2 = require('buffer'),
    Buffer = _require2.Buffer;

tape('convert from BufferList to BufferListStream', function (t) {
  var data = Buffer.from("TEST-".concat(Date.now()));
  var bl = new BufferList(data);
  var bls = new BufferListStream(bl);
  t.ok(bl.slice().equals(bls.slice()));
  t.end();
});
tape('convert from BufferListStream to BufferList', function (t) {
  var data = Buffer.from("TEST-".concat(Date.now()));
  var bls = new BufferListStream(data);
  var bl = new BufferList(bls);
  t.ok(bl.slice().equals(bls.slice()));
  t.end();
});